import 'package:flutter/material.dart';
import 'package:loading_animation_widget/loading_animation_widget.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      home: MyWidgetList(),
    );
  }
}

class MyWidgetList extends StatefulWidget {
  @override
  _MyWidgetListState createState() => _MyWidgetListState();
}

class _MyWidgetListState extends State<MyWidgetList> {
  List widgets = [
    Future.delayed(Duration(seconds: 2), () {
      return Container(
        height: 350,
        width: 350,
        margin: EdgeInsetsDirectional.symmetric(horizontal: 5),
        child: Image.network(
            'https://th.bing.com/th/id/OIP.E5YfX9KpZnxQoHbTJHQJwAHaEy?w=278&h=180&c=7&r=0&o=5&pid=1.7'),
      );
    }),
    Future.delayed(Duration(seconds: 5), () {
      return Row(
        mainAxisAlignment: MainAxisAlignment.spaceEvenly,
        children: [
          Container(
            height: 350,
            width: 350,
            margin: EdgeInsetsDirectional.symmetric(horizontal: 5),
            child: Image.network(
                'data:image/jpeg;base64,'),
          ),
          Container(
            height: 350,
            width: 350,
            margin: EdgeInsetsDirectional.symmetric(horizontal: 5),
            child: Image.network(
                'data:image/jpeg;base64,'),
          )
        ],
      );
    }),
    Future.delayed(Duration(seconds: 8), () {
      return Row(
        mainAxisAlignment: MainAxisAlignment.spaceEvenly,
        children: [
          Container(
            height: 350,
            width: 350,
            margin: EdgeInsetsDirectional.symmetric(horizontal: 5),
            child: Image.network(
                'data:image/jpeg;base64,'),
          ),
          Container(
            height: 350,
            width: 350,
            margin: EdgeInsetsDirectional.symmetric(horizontal: 5),
            child: Image.network(
                'https://th.bing.com/th/id/OIP.vufmMn0tAVAz9R3xHuu3TgHaEK?w=283&h=180&c=7&r=0&o=5&pid=1.7'),
          ),
          Container(
            height: 350,
            width: 350,
            margin: EdgeInsetsDirectional.symmetric(horizontal: 5),
            child: Image.network(
                'data:image/jpeg;base64,'),
          ),
        ],
      );
    }),
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        elevation: 0,
        title: Text(
          'Mohamed Amgad Assignment',
          style: TextStyle(
              fontSize: 25, color: Colors.black, fontWeight: FontWeight.bold),
        ),
        backgroundColor: Colors.white,
      ),
      body: SafeArea(
        child: Padding(
          padding: EdgeInsets.symmetric(horizontal: 100),
          child: ListView.separated(
            itemCount: widgets.length,
            itemBuilder: (context, index) {
              return FutureBuilder(
                future: widgets[index],
                builder: (context, snapshot) {
                  if (snapshot.connectionState == ConnectionState.done) {
                    return snapshot.data as Widget;
                  } else {
                    return Center(
                      child: LoadingAnimationWidget.inkDrop(
                        color: Colors.black,
                        size: 50,
                      ),
                    );
                  }
                },
              );
            },
            separatorBuilder: (BuildContext context, int index) {
              return SizedBox(height: 50);
            },
          ),
        ),
      ),
    );
  }
}
